import React, { useState} from 'react';
import { Modal } from 'react-bootstrap';
//import { useNavigate  } from 'react-router-dom';
import { Container, Table, Button } from 'react-bootstrap';
import '../App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
//import axios from 'axios';

const Profile = () =>{
    //let activeUserDetails = localStorage.getItem("userDetails");
    const myObject = JSON.parse(localStorage.getItem('userDetails'));
    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
    
    if(myObject){
        
        return(
            
            <Container className='col-sm-3'>
                <h5>User Profile</h5>
                <Table striped bordered hover size="sm">
                <thead>
                    <tr>
                    <th>First Name</th>
                    <th></th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                    <td>{myObject.fname}</td>
                    <td><button type="button" className="btn btn-primary btn-sm" onClick={handleShow}>Update</button></td>
                    </tr>
                </tbody>
                <thead>
                    <tr>
                    <th>Last Name</th>
                    <th></th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                    <td>{myObject.fname}</td>
                    <td><button type="button" className="btn btn-primary btn-sm">Update</button></td>
                    </tr>
                </tbody>
                <thead>
                    <tr>
                    <th>Email Address</th>
                    <th></th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                    <td>{myObject.email}</td>
                    <td><button type="button" className="btn btn-primary btn-sm">Update</button></td>
                    </tr>
                </tbody>
                </Table>
                <Modal show={show} onHide={handleClose}>
                    <Modal.Header closeButton>
                    <Modal.Title>Update</Modal.Title>
                    </Modal.Header>
                    <Modal.Body></Modal.Body>
                    <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                        Close
                    </Button>
                    <Button variant="primary" onClick={handleClose}>
                        Save Changes
                    </Button>
                    </Modal.Footer>
                </Modal>
            </Container>
            
        );
        
    }
    
}

export default Profile;
