import React, { useState} from 'react';
import { Modal } from 'react-bootstrap';
//import { useNavigate  } from 'react-router-dom';
import { Container, Table, Button, Form } from 'react-bootstrap';
import '../App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';

const Profile = () =>{
    //let activeUserDetails = localStorage.getItem("userDetails");
    const myObject = JSON.parse(localStorage.getItem('userDetails'));

    const [formData, setFormData] = useState({
      
        id: '',
        fname: '',
  
    });
    const handleChange = (e) => {
      const { name, value } = e.target;
      setFormData(prevState => ({
          ...prevState,
          [name]: value
      }));
    };
    const handleSubmit = async (e) => {
        e.preventDefault();
        // Axios request will go here.
        try {
          await axios.post('http://127.0.0.1:8000/api/updateUserData', formData);
          window.location.reload(false);
        } 
        catch (error) {
            console.error('Error submitting data', error);
            alert(error.response.data.message);
            // Handle the error (maybe show an error message to the user)
        }
    };
    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    //const handleShow = () => setShow(true);
    const fnameClick = (event, message) => {
		//alert(message);
        setShow(true);
        
        
        
	}
    
    if(myObject){
        
        return(
            
            <Container className='col-sm-3'>
                <h5>User Profile</h5>
                <Table striped bordered hover size="sm">
                <thead>
                    <tr>
                    <th>First Name</th>
                    <th></th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                    <td>{myObject.fname}</td>
                    <td><button type="button" className="btn btn-primary btn-sm" onClick={event => fnameClick(event,myObject.fname)}>Update</button></td>
                    </tr>
                </tbody>
                <thead>
                    <tr>
                    <th>Last Name</th>
                    <th></th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                    <td>{myObject.lname}</td>
                    <td><button type="button" className="btn btn-primary btn-sm">Update</button></td>
                    </tr>
                </tbody>
                <thead>
                    <tr>
                    <th>Email Address</th>
                    <th></th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                    <td>{myObject.email}</td>
                    <td><button type="button" className="btn btn-primary btn-sm">Update</button></td>
                    </tr>
                </tbody>
                </Table>
                
                <Modal show={show} onHide={handleClose}>
                    <form onSubmit={handleSubmit}>
                    <Modal.Header closeButton>
                    <Modal.Title>Update</Modal.Title>
                    </Modal.Header>
                    
                    <Form.Control type="hidden" name="id" value ={myObject.id} />
                    <Form.Control type="text" placeholder="First Name" name="fname" value ={formData.fname} onChange={handleChange} />
                    
                    <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                        Close
                    </Button>
                    <Button variant="primary" type="submit">
                        Save Changes
                    </Button>
                    </Modal.Footer>
                    </form>
                </Modal>
                
            </Container>
            
        );
        
    }
    
}

export default Profile;
