        setShow(true);
        
        
        
	}
    
    if(myObject){
        
        return(
            
            <Container className='col-sm-3'>
                <h5>User Profile</h5>
                
                <Row className="vh-100 d-flex justify-content-center align-items-center moveTop">
                    <Col md={8} lg={6} xs={12}>
                    <Card className="px-4">
                        <Card.Body>
                        <div className="mb-3 mt-md-4">
                            <h2 className="fw-bold mb-2 text-center text-uppercase ">
                            Registration Form
                            </h2>
                            <div className="mb-3">
                            <form>
                                <Form.Group className="mb-3" controlId="fName">
                                
                                <Form.Control type="text" name="fname" value ={myObject.fname} />
                                </Form.Group>

                                <Form.Group className="mb-3" controlId="lName">
                                
                                <Form.Control type="text" name="lname" value ={myObject.lname} />
                                </Form.Group>

                                <Form.Group className="mb-3" controlId="formBasicEmail">
                                
                                <Form.Control type="email" name ="email" value={myObject.email} />
                                </Form.Group>
                                
                            </form>
                            
                            </div>
                        </div>
                        </Card.Body>
                    </Card>
                    </Col>
                </Row>
                
                <Modal show={show} onHide={handleClose}>
                    <form onSubmit={handleSubmit}>
                    <Modal.Header closeButton>
                    <Modal.Title>Update</Modal.Title>
                    </Modal.Header>
                    
                    <Form.Control type="hidden" name="id" value ={myObject.id} />
                    <Form.Control type="text" placeholder="First Name" name="fname" value ={formData.fname} onChange={handleChange} />
                    
                    <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                        Close
                    </Button>
                    <Button variant="primary" type="submit">
                        Save Changes
                    </Button>
                    </Modal.Footer>
                    </form>
                </Modal>
                
            </Container>
            
        );
        
    }
    
}

export default Profile;
