import { Col, Button, Row, Container, Card, Form, Modal } from 'react-bootstrap';
import axios from 'axios';
import React, { useState } from 'react';




  const Registration = () =>{
  const RegistrationForm = ({ onSubmit }) => {
  const [formData, setFormData] = useState({
      name: '',
      email: '',
      password: '',
  });
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
        ...prevState,
        [name]: value
    }));
  };
  }
  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const onRegistrationSubmit = (e) => {
    e.preventDefault();
    handleClose();
  };

  return (
    <>
      <div
        className="d-flex align-items-center justify-content-center"
        style={{ height: "100vh" }}
      >
        <Button variant="primary" onClick={handleShow}>
          Launch Form modal
        </Button>
      </div>
      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Login Form</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <RegistrationForm onSubmit={onRegistrationSubmit} />
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close Modal
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Axios request will go here.
    try {
      const response = await axios.post('http://127.0.0.1:8000/api/register', formData);
      console.log('Data submitted successfully', response.data);
      return (
      <div
        className="modal show"
        style={{ display: 'block', position: 'initial' }}
      >
        <Modal.Dialog>
          <Modal.Header closeButton>
           
          </Modal.Header>

          <Modal.Body>
            <p>Account Successfully Created!</p>
          </Modal.Body>

          <Modal.Footer>
            <Button variant="secondary">Okay</Button>
          </Modal.Footer>
        </Modal.Dialog>
      </div>
      );
      
      
      // Handle successful submission (maybe reset the form or navigate to another page)
  } catch (error) {
      console.error('Error submitting data', error);
      return(
        
      <div
        className="modal show"
        style={{ display: 'block', position: 'initial' }}
      >
        <Modal.Dialog>
          <Modal.Header closeButton>
            
          </Modal.Header>

          <Modal.Body>
            <p>Email Address was already Used!</p>
          </Modal.Body>

          <Modal.Footer>
            <Button variant="secondary">Okay</Button>
          </Modal.Footer>
        </Modal.Dialog>
      </div>
      );
      // Handle the error (maybe show an error message to the user)
  }
  };
  
    return (

     
            <Container>
              <Row className="vh-100 d-flex justify-content-center align-items-center">
                <Col md={8} lg={6} xs={12}>
                  <Card className="px-4">
                    <Card.Body>
                      <div className="mb-3 mt-md-4">
                        <h2 className="fw-bold mb-2 text-center text-uppercase ">
                          Logo
                        </h2>
                        <div className="mb-3">
                          <form onSubmit={handleSubmit}>
                            <Form.Group className="mb-3" controlId="Name">
                              
                              <Form.Control type="text" placeholder="Enter Name" name="name" value ={formData.name} onChange={handleChange} />
                            </Form.Group>

                            <Form.Group className="mb-3" controlId="formBasicEmail">
                              
                              <Form.Control type="email" placeholder="Enter email" name ="email" value={formData.email} onChange={handleChange} />
                            </Form.Group>

                            <Form.Group
                              className="mb-3"
                              controlId="formBasicPassword"
                            >
                              
                              <Form.Control type="password" placeholder="Password" name="password" value={formData.password} onChange={handleChange}/>
                            </Form.Group>
                            

                            <div className="d-grid">
                              <Button variant="primary" type="submit">
                                Create Account
                              </Button>
                            </div>
                          </form>
                          <div className="mt-3">
                            <p className="mb-0  text-center">
                              Already have an account??{' '}
                              <a href="{''}" className="text-primary fw-bold">
                                Sign In
                              </a>
                            </p>
                          </div>
                        </div>
                      </div>
                    </Card.Body>
                  </Card>
                </Col>
              </Row>
            </Container>
        

    );
    }
    export default Registration;
