import React, { useState } from 'react';
import { useNavigate  } from 'react-router-dom';
import '../App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
//import Swal from 'sweetalert2';
import axios from 'axios';


const Login = () =>{

  const nav = useNavigate();
  const [userId, setUser] = useState();
  const [userName, setName] = useState();

  const [formData, setFormData] = useState({
      
      email: '',
      password: '',

  });
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
        ...prevState,
        [name]: value
    }));
  };
  

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Axios request will go here.
    try {
      const response = await axios.post('http://127.0.0.1:8000/api/login', formData);
      console.log('Data submitted successfully', response.data.data.id);
      setUser(response.data.data.id);
      setName(response.data.data.name);
      localStorage.setItem('userId', response.data.data.id);
      localStorage.setItem('userName', response.data.data.name);
      alert("Successfully Login");
      nav("/");
      window.location.reload(false);
      
      
      
      
      // Handle successful submission (maybe reset the form or navigate to another page)
  } catch (error) {
      console.error('Error submitting data', error);
      alert(error.response.data.message);
      // Handle the error (maybe show an error message to the user)
  }
  };
  
  if(!userId && !userName){
    return (
    
      <div className="maincontainer">
        
        
        <div className="container p-5">
        <h1 className="text-center mb-5">LOGIN FORM</h1>
        <form onSubmit={handleSubmit}>
        <div class="mb-3">
          <input type="email" name="email" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Enter Email" value={formData.email} onChange={handleChange} />
        </div>
        <div class="mb-3">
          <input type="password" name="password" class="form-control" id="exampleInputPass1" aria-describedby="passHelp" placeholder="Enter Password" value={formData.password} onChange={handleChange} />
        </div>
        <button type="submit" className="btn btn-primary" >Submit</button>
      </form>
       
            
      </div>
     
      </div>
      
    );
  }
}
export default Login;
