import Container from 'react-bootstrap/Container';
import Navbar from 'react-bootstrap/Navbar';
import { Link } from 'react-router-dom';
import React, { useState, useEffect } from 'react';

function Header(){
  useEffect(() => {
    //const loggedInUserName = localStorage.getItem("userName");
    const loggedInUserId = localStorage.getItem("userId");
    if (loggedInUserId) {
      //const foundUserName = JSON.stringify(loggedInUserName);
      const foundUserId = JSON.stringify(loggedInUserId);
      //setUserName(foundUserName);
      setUserId(foundUserId);
    }
  }, []);
  //const [userName, setUserName] = useState();
  let activeId = localStorage.getItem("userId");
  const [userId, setUserId] = useState();
  console.log(activeId);

  const handleLogout = () => {
    //setUserName({});
    setUserId({});
    localStorage.clear();
    window.location.reload(false);
  };

  if(userId){
    return(
      <div>
      <Navbar className="bg-body-tertiary">
      <Container>
        <Navbar.Brand to="/">Home</Navbar.Brand>
        <Navbar.Toggle />
        <Navbar.Collapse className="justify-content-end urlCss">
            
        <Link to="/user">My Account</Link>&nbsp;&nbsp;&nbsp;
        <Link onClick={handleLogout}>Logout</Link>

        </Navbar.Collapse>
      </Container>
      </Navbar>
    </div>
    ); 
  }
  return (
      <div>
        <Navbar className="bg-body-tertiary">
        <Container>
          <Navbar.Brand to="/">Home</Navbar.Brand>
          <Navbar.Toggle />
          <Navbar.Collapse className="justify-content-end urlCss">
              
              <Link to="/login">Login</Link>
              <Link to="/registration">Register</Link>

          </Navbar.Collapse>
        </Container>
        </Navbar>
      </div>
    );
}
export default Header;
