import * as React from "react";
import Navbar from "react-bootstrap/Navbar";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import "bootstrap/dist/css/bootstrap.css";

import { BrowserRouter as Router , Routes, Route, Link } from "react-router-dom";

import EditReact from "../views/EditReact";
import CreateReact from "../views/CreateReact";
import ListReact from "../views/ListReact";

function Home() {
  return (
  <Router>
    <Navbar bg="primary">
      <Container>
        <Link to={"/"} className="navbar-brand text-white">
          Basic Crud App
        </Link>
      </Container>
    </Navbar>

    <Container className="mt-5">
      <Row>
        <Col md={12}>
          <Routes>
            <Route path="/create" element={<CreateReact />} />
            <Route path="/edit/:id" element={<EditReact />} />
            <Route exact path='/' element={<ListReact />} />
          </Routes>
        </Col>
      </Row>
    </Container>
  </Router>
  );
}

export default Home;
