import {
  MDBBtn,
  MDBContainer,
  MDBRow,
  MDBCol,
  MDBCard,
  MDBCardBody,
  MDBInput
} from 'mdb-react-ui-kit';
import axios from 'axios';
import React, { useState } from 'react';



  const Registration = () =>{

  const [formData, setFormData] = useState({
      name: '',
      email: '',
      password: '',
  });
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
        ...prevState,
        [name]: value
    }));
  };
  
  const handleSubmit = async (e) => {
    e.preventDefault();
    // Axios request will go here.
    try {
      const response = await axios.post('http://127.0.0.1:8000/api/register', formData);
      console.log('Data submitted successfully', response.data);
      // Handle successful submission (maybe reset the form or navigate to another page)
  } catch (error) {
      console.error('Error submitting data', error);
      // Handle the error (maybe show an error message to the user)
  }
  };
  
    return (
    
      <MDBContainer fluid>

      <MDBRow className='justify-content-center align-items-center m-5'>

        <MDBCard>
          <MDBCardBody className='px-4'>

            <h3 className="fw-bold mb-4 pb-2 pb-md-0 mb-md-5">Registration Form</h3>
            <form onSubmit={handleSubmit}>
            <MDBRow>
            
              <MDBCol md='6'>
                <MDBInput wrapperClass='mb-4' label='Name' size='lg' id='form1' type='text' name='name' value={formData.name} onChange={handleChange}/>
              </MDBCol>
            
            </MDBRow>

            <MDBRow>

              <MDBCol md='6'>
                <MDBInput wrapperClass='mb-4' label='Email' size='lg' id='form4' type='email' name='email' value={formData.email} onChange={handleChange} />
              </MDBCol>
            
            </MDBRow>

            <MDBRow>

              <MDBCol md='6'>
                <MDBInput wrapperClass='mb-4' label='Password' size='lg' id='form4' type='password' name='password' value={formData.password} onChange={handleChange} />
              </MDBCol>
            
            </MDBRow>

            <MDBBtn className='mb-4' size='lg' type='submit'>Submit</MDBBtn>
            </form>
          </MDBCardBody>
        </MDBCard>

      </MDBRow>
      </MDBContainer>      



    );
    }
    export default Registration;
