import React from 'react';
import '../App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import Swal from 'sweetalert2';
import axios from 'axios';


class Login extends React.Component {
  constructor(props)
    {
      super(props);
      this.addFormData = this.addFormData.bind(this);
      this.textInput = null;
      this.myEmail = element => {
        this.textInput = element;
      };
  
      this.password = () => {
        // Focus the text input using the raw DOM API
        if (this.textInput) this.textInput.focus();
      };
    }
    componentDidMount() {
      // autofocus the input on mount
      this.focusTextInput();
    }
  //Form Submission
  addFormData(evt)
    {
      evt.preventDefault();
      const fd = new FormData();
      fd.append('email', this.myEmail.value);
      fd.append('password', this.password.value);
      
      //call api
      axios.post('http://127.0.0.1:8000/api/login', fd
      ).then(res=>
      {
      //Success alert
      if(res["data"]["status"] === "error")
      {
        Swal.fire({
          title: 'OPPS',
          text:   'Error',
          type: 'warning',
        
      });
      }
      else
      {
        Swal.fire({
        title: 'WOW',
        text:   "You have been logged-in successfully",
        type: 'success',
        });
    }
    this.myFormRef.reset();
    }
    );
    }
 
  render() {
   
    return (
    
      <div className="maincontainer">
        
        
        <div className="container p-5">
        <h1 className="text-center mb-5">LOGIN FORM</h1>
        <form ref={(el) => this.myFormRef = el}>
        <div class="mb-3">
          <input type="email" name="email" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Enter Email" ref="{this.myEmail}" />
        </div>
        <div class="mb-3">
          <input type="password" name="password" class="form-control" id="exampleInputPass1" aria-describedby="passHelp" placeholder="Enter Password" ref="{this.password}" />
        </div>
        <button type="submit" className="btn btn-primary" onClick={this.addFormData}>Submit</button>
      </form>
       
            
      </div>
     
      </div>
      
)
};
}
export default Login;
