                    <form onSubmit={handleSubmit}>
                    <Modal.Header closeButton>
                    <Modal.Title>Update</Modal.Title>
                    </Modal.Header>
                    
                    <Form.Control type="hidden" name="id" value ={myObject.id} />
                    <Form.Control type="text" placeholder="First Name" name="fname" value ={formData.fname} onChange={handleChange} />
                    
                    <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                        Close
                    </Button>
                    <Button variant="primary" type="submit">
                        Save Changes
                    </Button>
                    </Modal.Footer>
                    </form>
                </Modal>
                
            </Container>
            
        );
        
    }
    
}

export default Profile;
