import React, { useState } from "react";
import { Col, Button, Row, Container, Card, Form } from 'react-bootstrap';
import axios from "axios";

const MyForm = () =>{

    //const [userId, setUser] = useState();
    const [formData, setFormData] = useState({
        first_name: '',
        last_name: '',
        email: '',
    });
    
    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prevState => ({
            ...prevState,
            [name]: value
        }));
    };
    const handleSubmit = async (e) => {
    e.preventDefault();
    // Axios request will go here.
    try {
        const response = await axios.post('http://127.0.0.1:8000/api/addCostumer', formData);
        console.log('Data submitted successfully', response.data);
        
        window.location.reload(false);
        // Handle successful submission (maybe reset the form or navigate to another page)
    } catch (error) {
        console.error('Error submitting data', error);
        alert(error.response.data.message);
        // Handle the error (maybe show an error message to the user)
    }
    };
    
        return(
            <Container>
              <Row className="vh-100 d-flex justify-content-center align-items-center">
                <Col md={2} lg={12} xs={2}>
                  <Card className="px-4">
                    <Card.Body>
                      <div className="mb-3 mt-md-4">
                        <h2 className="fw-bold mb-2 text-center text-uppercase ">
                          Add Data
                        </h2>
                        <div className="mb-3">
                          <form onSubmit={handleSubmit}>
                            <Form.Group className="mb-3" controlId="fName">
                              <Form.Control type="text" placeholder="Enter First Name" name="first_name"  value ={formData.first_name} onChange={handleChange}/>
                            </Form.Group>

                            <Form.Group className="mb-3" controlId="fName">
                              <Form.Control type="text" placeholder="Enter Last Name" name="last_name" value ={formData.last_name} onChange={handleChange}/>
                            </Form.Group>

                            <Form.Group className="mb-3" controlId="formBasicEmail">
                              <Form.Control type="email" placeholder="Enter email" name ="email"  value ={formData.email} onChange={handleChange}/>
                            </Form.Group>

                            <div className="d-grid">
                              <Button variant="primary" type="submit">
                                Save
                              </Button>
                            </div>
                            {/*register ? (
                                <p className="text-success">You Are Registered Successfully</p>
                            ) :  ( 
                                <p className="text-danger">You Are Not Registered</p>
                            )*/}
                          </form>
                          
                        </div>
                      </div>
                    </Card.Body>
                  </Card>
                </Col>
              </Row>
            </Container>
        );
    
}

export default MyForm;
