import React, { useState } from 'react';
//import { useNavigate  } from 'react-router-dom';
import { Container, Table } from 'react-bootstrap';
import '../App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';

const Profile = () =>{
    let activeId = localStorage.getItem("userId");
    const [formData, setFormData] = useState({
      
        id: activeId,
  
    });
    setFormData(activeId);
    //const [userName, setUserName] = useState();
    
    //const [userId, setUserId] = useState();
    //console.log(activeId);
    try {
        const response = axios.post('http://127.0.0.1:8000/api/getUserData', formData);
        console.log('Data submitted successfully', response);
        
        
        
        
        // Handle successful submission (maybe reset the form or navigate to another page)
    } catch (error) {
        console.error('Error submitting data', error);
        alert(error.response.data.message);
        // Handle the error (maybe show an error message to the user)
    }
    return(
        <Container className='col-sm-3'>
            <h5>User Profile</h5>
            <Table striped bordered hover size="sm">
            <thead>
                <tr>
                <th>MArk</th>
                <th></th>
                </tr>
            </thead>
            <tbody>
                <tr>
                <td>Mark</td>
                <td><button type="button" className="btn btn-primary btn-sm">Update</button></td>
                </tr>
            </tbody>
            <thead>
                <tr>
                <th>Last Name</th>
                <th></th>
                </tr>
            </thead>
            <tbody>
                <tr>
                <td>Mark</td>
                <td><button type="button" className="btn btn-primary btn-sm">Update</button></td>
                </tr>
            </tbody>
            <thead>
                <tr>
                <th>Email Address</th>
                <th></th>
                </tr>
            </thead>
            <tbody>
                <tr>
                <td>Mark</td>
                <td><button type="button" className="btn btn-primary btn-sm">Update</button></td>
                </tr>
            </tbody>
            </Table>
        </Container>
        
    );
}
export default Profile;
