import React, { useState, useEffect } from 'react';
import { useHistory } from 'react-router-dom';
import '../App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
//import Swal from 'sweetalert2';
import axios from 'axios';


function Login() {

  const [email,setEmail] = useState("");
  const [password,setPassword] = useState("");
  const history = useHistory();

  useEffect(() => {
    if(localStorage.getItem("user-info")){
      history.push("/add");
    }
  }, [])

  async function login() {
    let item = {email,password};
    let result = await fetch('http://127.0.0.1:8000/api/login',{ 
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
              'Accept': 'application/json'
            },
            body: JSON.stringify(item)
            }
            );
            result = await result.json();
            localStorage.setItem("user-info",JSON.stringify(result));
            //history.push("/add");
  }
    return (
    
      <div className="col-sm-6 offset-sm-3">
        
        
        <div className="container p-5">
        <h1 className="text-center mb-5">LOGIN FORM</h1>
        <form>
        <div class="mb-3">
          <input type="email" className="form-control" placeholder="Enter Email" onChange={setEmail(target.value)} />
        </div>
        <div class="mb-3">
        <input type="password" className="form-control" placeholder="Enter Email" onChange={setPassword(target.value)} />
        </div>
        <button type="submit" className="btn btn-primary" onClick={login} >Submit</button>
        </form>
       
            
        </div>
     
      </div>
      
);

}
export default Login;
