import React, { useState } from 'react';
import { useNavigate  } from 'react-router-dom';
import { Col, Button, Row, Container, Card, Form } from 'react-bootstrap';
import '../App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
//import Swal from 'sweetalert2';
import axios from 'axios';


const Login = () =>{

  const nav = useNavigate();
  const [userId, setUser] = useState();

  const [formData, setFormData] = useState({
      
      email: '',
      password: '',

  });
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
        ...prevState,
        [name]: value
    }));
  };
  

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Axios request will go here.
    try {
      const response = await axios.post('http://127.0.0.1:8000/api/login', formData);
      console.log('Data submitted successfully', response.data.data.id);
      const arr = {
        id: response.data.data.id,
        fname: response.data.data.fname,
        lname: response.data.data.lname,
        email: response.data.data.email,
        verified: response.data.data.email_verified_at
      };
      setUser(response.data.data.id);
      localStorage.setItem('userDetails', JSON.stringify(arr));
      alert("Successfully Login");
      nav("/");
      window.location.reload(false);
      
      
      
      
      // Handle successful submission (maybe reset the form or navigate to another page)
  } catch (error) {
      console.error('Error submitting data', error);
      alert(error.response.data.message);
      // Handle the error (maybe show an error message to the user)
  }
  };
  
  if(!userId){
    return (
      <Container>
              <Row className="vh-100 d-flex justify-content-center align-items-center moveTop">
                <Col md={8} lg={6} xs={12}>
                  <Card className="px-4">
                    <Card.Body>
                      <div className="mb-3 mt-md-4">
                        <h2 className="fw-bold mb-2 text-center text-uppercase ">
                          Login
                        </h2>
                        <div className="mb-3">
                          <form onSubmit={handleSubmit}>
                            <Form.Group className="mb-3" controlId="email">
                              
                              <Form.Control type="text" placeholder="Email Address" name="email" value ={formData.email} onChange={handleChange} />
                            </Form.Group>

                            <Form.Group className="mb-3" controlId="password">
                              
                              <Form.Control type="password" placeholder="Password" name="password" value ={formData.password} onChange={handleChange} />
                            </Form.Group>

                            <div className="d-grid">
                              <Button variant="primary" type="submit">
                                Submit
                              </Button>
                            </div>
                            {/*register ? (
                                <p className="text-success">You Are Registered Successfully</p>
                            ) :  ( 
                                <p className="text-danger">You Are Not Registered</p>
                            )*/}
                          </form>
                          
                        </div>
                      </div>
                    </Card.Body>
                  </Card>
                </Col>
              </Row>
            </Container>
        
        
        
      
    );
  }
}
export default Login;
